  0: 'Vac1 /L1 Phase voltage',
  1: 'Iac1 /L1 Phase current',
  2: 'Fac1 /L1 Phase frequency',
  3: 'Vpv1 /PV1 input voltage',
  4: 'Vpv2 /PV2 input voltage',
