const benchmarks = Array.isArray(flask.benchmarks) ? flask.benchmarks : [];

// Stats
const totalUploads = uploads.length;
const latest = totalUploads ? uploads[uploads.length - 1] : null;
const lastUploadTs = latest ? latest.timestamp : 'None';

// Group uploads by device; sort devices by latest activity (desc) and uploads by ts (desc)
const deviceGroups = new Map();
for (const u of uploads) {
  const id = u.device_id || 'Unknown-Device';
  if (!deviceGroups.has(id)) deviceGroups.set(id, []);
  deviceGroups.get(id).push(u);
}
const deviceOrder = [...deviceGroups.entries()].map(([id, list]) => {
  const latestTs = list.reduce((m, r) => Math.max(m, new Date(r.timestamp).getTime()||0), 0);
  return { id, list, latestTs };
}).sort((a,b)=> b.latestTs - a.latestTs);

// Limit recent view to the newest ~10 uploads overall, but grouped under devices
// Build a flattened list of uploads sorted desc to compute the cap, then slice per device accordingly
const sortedAll = uploads.slice().sort((a,b)=> new Date(b.timestamp)-new Date(a.timestamp));
const capUploads = sortedAll.slice(0, 10);
const capSet = new Set(capUploads.map(u => `${u.device_id||'Unknown-Device'}@@${u.timestamp}`));

let deviceHtml = '';
if (deviceOrder.length === 0) {
  deviceHtml = '<tr><td colspan="5" style="text-align:center;color:#7f8c8d;font-style:italic;">No uploads yet</td></tr>';
} else {
  let globalIndex = 0;
  for (const { id: devId, list } of deviceOrder) {
    // Filter each device's list by the overall cap set and sort desc
    const listFiltered = list
      .filter(u => capSet.has(`${u.device_id||'Unknown-Device'}@@${u.timestamp}`))
      .sort((a,b)=> new Date(b.timestamp)-new Date(a.timestamp));
    if (!listFiltered.length) continue;

    const lastSeen = new Date(listFiltered[0].timestamp).toLocaleString();
    const totalForDevice = list.length;

    // Device header row (non-click)
    deviceHtml += `
      <tr class="device-row">
        <td class="device-cell" colspan="5">
          <div class="dev-header">
            <div class="dev-title">${devId}</div>
            <div class="dev-meta">Last seen: ${lastSeen} ‚Ä¢ Total uploads: ${totalForDevice}</div>
          </div>
        </td>
      </tr>`;

    // Upload rows under device (each expandable)
    listFiltered.forEach((u, idx) => {
      const uid = `dev_${globalIndex}_u_${idx}`;
      const samplesCount = Array.isArray(u.samples) ? u.samples.length : 0;

      // Build preview chips grouped by register
      let regMap = new Map();
      (u.samples || []).forEach(s => {
        const key = s.reg_addr;
        if (!regMap.has(key)) regMap.set(key, []);
        const val = (typeof s.value === 'number') ? s.value.toFixed(3) : s.value;
        regMap.get(key).push({ ts: s.timestamp, v: val });
      });

      const detailSections = [...regMap.entries()].map(([reg, arr]) => {
        const chips = arr.slice(0, 12).map(p => `<span class=\"chip\" title=\"ts:${p.ts}\">${p.v}</span>`).join('');
        return `
          <div class=\"reg-block\">
            <div class=\"reg-title\">${REG_NAMES[reg] || ('Reg '+reg)}</div>
            <div class=\"chip-row\">${chips || '<span class=\"chip\">(no values)</span>'}</div>
          </div>`;
      }).join('');

      deviceHtml += `
        <tr class=\"data-row\" onclick=\"toggleDetails('${uid}')\">
          <td class=\"expand-cell\"><span class=\"expand-btn\" id=\"btn_${uid}\">‚ñ∂</span></td>
          <td>${new Date(u.timestamp).toLocaleString()}</td>
          <td><strong>${u.device_id || 'Unknown-Device'}</strong></td>
